Exercise.create!([
  { :title => "Fool mate - no lesson", :description => "http://en.wikipedia.org/wiki/Fool%27s_Mate", :start => "a8-black-♜,b8-black-♞,c8-black-♝,d8-black-♛,e8-black-♚,f8-black-♝,g8-black-♞,h8-black-♜,a7-black-♟,b7-black-♟,c7-black-♟,d7-black-♟,e7-black-♟,f7-black-♟,g7-black-♟,h7-black-♟,a2-white-♟,b2-white-♟,c2-white-♟,d2-white-♟,e2-white-♟,f2-white-♟,g2-white-♟,h2-white-♟,a1-white-♜,b1-white-♞,c1-white-♝,d1-white-♛,e1-white-♚,f1-white-♝,g1-white-♞,h1-white-♜", :lesson_id => nil, :created_at => "2012-11-09 22:07:09", :updated_at => "2013-09-18 16:47:47", :next_exercise => "104", :sort_for_lesson => 2, :conclusion => "" },
  { :title => "Rook'em, Dann-O!", :description => "It's two on one: ♜+♚ versus just ♚.  White has the clear advantage (here's hoping you are white). The key is to pin the Black's ♚ to any board side and move in for the kill.", :start => "h8-black-♚,a1-white-♜,h1-white-♚", :lesson_id => 2, :created_at => "2013-03-10 18:34:27", :updated_at => "2013-09-11 20:27:28", :next_exercise => "3", :sort_for_lesson => 1, :conclusion => "The shown sequence is the best possible choices for both sides.  It took 10 moves for White to establish mate.  Black has an unsurmountable disadvantage, but the point of the lesson is to show how powerful the ♜ can be - its ability to confine pieces and shutdown entire parts of the board is very powerful." },
  { :title => "Totally Rook'd", :description => "Still two on one: ♜+♚ versus just ♚. White again has the clear advantage, but black has more flexibility finding their ♚ in the middle of the board. The goal is still to force confinement, but the process is more difficult as the white ♚ must push black to a board edge", :start => "e5-black-♚,e1-white-♚,h1-white-♜", :lesson_id => 2, :created_at => "2013-03-20 03:02:26", :updated_at => "2013-09-16 04:59:39", :next_exercise => "4", :sort_for_lesson => 2, :conclusion => "Mate here is established by using the ♜ as a confinement agent.  The goal is the same as the last exercise: push to the board edge, force the black ♚ into a lane (rank or file), and then remove that lane with the ♜" },
  { :title => "The Bishop Boys", :description => "♝, ♝, and ♚ versus ♚.  Since ♝ and ♚ alone cannot mate (there are just too many holes for the opposing ♚ to escape) the ♝s must be used in concert with the ♚ used as protection (since two bishops will be on opposite square colors and not able to protect each other).", :start => "h8-black-♚,c1-white-♝,e1-white-♚,f1-white-♝", :lesson_id => 2, :created_at => "2013-03-23 02:28:58", :updated_at => "2013-09-16 18:17:36", :next_exercise => "5", :sort_for_lesson => 3, :conclusion => "" },
  { :title => "The Queen Bee", :description => "♛+♚ versus ♚.  The ♛ is the strongest of the pieces (in terms of movement) and therefore should need the fewest number of moves to accomplish mate.", :start => "e5-black-♚,e1-white-♚,h1-white-♛", :lesson_id => 2, :created_at => "2013-03-23 02:53:57", :updated_at => "2013-09-18 03:24:36", :next_exercise => "undefined", :sort_for_lesson => 4, :conclusion => "Here, again, the key to this mate is pressing the black ♚ to the board's edge.  Mate happens faster with the ♛ than the ♜ due to it being a much more powerful piece.  In each case it is important to coordinate the ♚ as protection." },
  { :title => "Promotions: Power of the Pawn", :description => "Here white has the advantage as the KING can protect the PAWN because it is infront (between the white PAWN and black KING).  This example white will win by getting the PAWN to promotion.", :start => "f6-black-♚,f4-white-♚,e2-white-♟", :lesson_id => 8, :created_at => "2013-03-25 14:57:47", :updated_at => "2013-05-21 22:49:21", :next_exercise => "7", :sort_for_lesson => 1, :conclusion => "At the end of this there is a King/Queen vs. King set up - and checkmate should be established without issue." },
  { :title => "Pawn and Forced Stalemate", :description => "An opponents advantage of a PAWN can be nullified if you can get your KING in front of the PAWN.  This exercise shows how to force a stalemate in an un-winnable, but manageable set up.", :start => "e4-black-♚,d2-white-♚,e2-white-♟", :lesson_id => 8, :created_at => "2013-03-25 14:59:33", :updated_at => "2013-05-21 22:49:21", :next_exercise => "14", :sort_for_lesson => 2, :conclusion => nil },
  { :title => "Pawns: 1 vs 2", :description => "Positioning is key to all of chess, but especially when the end game is pawns vs pawn.  The advantage comes from using positioning to force the outcome you want. *Note: Here (as in most exercises) the opponent makes the \"smartest\" move available - other moves are possible, but would force mate sooner.", :start => "f8-black-♚,g7-black-♟,e6-white-♚,g6-white-♟,f5-white-♟", :lesson_id => 8, :created_at => "2013-04-04 21:52:53", :updated_at => "2013-05-21 22:48:49", :next_exercise => "9", :sort_for_lesson => 4, :conclusion => "" },
  { :title => "Pawns: 1 vs 2, Kings distant", :description => "This exercise has only a one PAWN advantage.  The key is not looking for PAWN promotion immediately, but instead being willing to sacrifice one of the PAWNs for KING positioning (in front of the remaining PAWN).", :start => "g7-black-♟,d6-black-♚,d4-white-♚,f4-white-♟,g4-white-♟", :lesson_id => 8, :created_at => "2013-04-10 02:52:41", :updated_at => "2013-05-21 22:48:49", :next_exercise => "10", :sort_for_lesson => 5, :conclusion => "This lesson exemplifies how few pieces can have such a dramatic impact on the outcome of the game (win versus draw).  Surmounting the odds requires very specific moves that force the submission of the opponent." },
  { :title => "Pawns: 2 vs 3", :description => "", :start => "g7-black-♟,h7-black-♟,d6-black-♚,d4-white-♚,f4-white-♟,g4-white-♟,h4-white-♟", :lesson_id => 8, :created_at => "2013-04-10 03:52:38", :updated_at => "2013-05-21 22:48:49", :next_exercise => "11", :sort_for_lesson => 6, :conclusion => "" },
  { :title => "Pawns: 2 vs 3, Split", :description => "A rule of thumb here is to act on the side of superiority. ", :start => "a7-black-♟,h7-black-♟,e6-black-♚,e4-white-♚,a2-white-♟,g2-white-♟,h2-white-♟", :lesson_id => 8, :created_at => "2013-04-12 02:59:08", :updated_at => "2013-05-21 22:48:49", :next_exercise => "undefined", :sort_for_lesson => 7, :conclusion => "" },
