  { :piece_white => "...", :starting_coordinate_white => nil, :ending_coordinate_white => nil, :explanation => "If ♚ - ♞ 1, ♟ - ♞ 7.", :exercise_id => 41, :move_number => 15, :computer => nil, :created_at => "2013-07-17 20:41:21", :updated_at => "2013-07-17 20:41:21", :piece_black => "♟", :starting_coordinate_black => "h4", :ending_coordinate_black => "g3" },
