  { :piece_white => "♜", :starting_coordinate_white => "h7", :ending_coordinate_white => "h5", :explanation => nil, :exercise_id => 80, :move_number => 3, :computer => nil, :created_at => "2013-07-17 20:41:22", :updated_at => "2013-07-17 20:41:22", :piece_black => "♚", :starting_coordinate_black => "c5", :ending_coordinate_black => "c4" },
