  { :piece_white => "♟", :starting_coordinate_white => "e2", :ending_coordinate_white => "e3", :explanation => nil, :exercise_id => 90, :move_number => 5, :computer => nil, :created_at => "2013-07-17 20:41:22", :updated_at => "2013-07-17 20:41:22", :piece_black => "♞", :starting_coordinate_black => "b8", :ending_coordinate_black => "d7" },
