  { :piece_white => "...", :starting_coordinate_white => nil, :ending_coordinate_white => nil, :explanation => "Not the most favoured move, but a perfectly natural developing one, and consequently it cannot be bad.", :exercise_id => 94, :move_number => 5, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♟", :starting_coordinate_black => "d5", :ending_coordinate_black => "e4" },
  { :piece_white => "♞", :starting_coordinate_white => "c3", :ending_coordinate_white => "e4", :explanation => "♟ - ♛ ♝ 4 is generally played in this case instead of the text move.", :exercise_id => 94, :move_number => 6, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♞", :starting_coordinate_black => "b8", :ending_coordinate_black => "d7" },
