  { :piece_white => "♞", :starting_coordinate_white => "e4", :ending_coordinate_white => "f3", :explanation => nil, :exercise_id => 94, :move_number => 8, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♝", :starting_coordinate_black => "c8", :ending_coordinate_black => "e7" },
  { :piece_white => "♛", :starting_coordinate_white => "d1", :ending_coordinate_white => "e2", :explanation => nil, :exercise_id => 94, :move_number => 9, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 21:14:07", :piece_black => "...", :starting_coordinate_black => nil, :ending_coordinate_black => nil },
  { :piece_white => "...", :starting_coordinate_white => nil, :ending_coordinate_white => nil, :explanation => "This is played to prevent ♟ - ♛ ♞ 3, followed by ♝ - ♞ 2, which is the general form of development for Black in this variation. If Black now plays 8...♟ - ♛ ♞ 3; 9. ♝ - ♞ 5 ch, ♝ - ♛ 2; 10. ♞ - ♚ 5 and White obtains a considerable advantage in position.", :exercise_id => 94, :move_number => 10, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♚", :starting_coordinate_black => "d8", :ending_coordinate_black => "g8" },
  { :piece_white => "♝", :starting_coordinate_white => "c1", :ending_coordinate_white => "g5", :explanation => nil, :exercise_id => 94, :move_number => 11, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♟", :starting_coordinate_black => "h7", :ending_coordinate_black => "h6" },
  { :piece_white => "♝", :starting_coordinate_white => "g5", :ending_coordinate_white => "f6", :explanation => "Of course Black could not play ♟ - ♛ ♞ 3 because of ♝ x ♞, followed by ♛ - ♚ 4.", :exercise_id => 94, :move_number => 12, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♝", :starting_coordinate_black => "e7", :ending_coordinate_black => "f6" },
  { :piece_white => "♛", :starting_coordinate_white => "e2", :ending_coordinate_white => "e4", :explanation => nil, :exercise_id => 94, :move_number => 13, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♟", :starting_coordinate_black => "g7", :ending_coordinate_black => "g6" },
  { :piece_white => "♟", :starting_coordinate_white => "h2", :ending_coordinate_white => "h4", :explanation => "This weakens Black's King's side. ♜ - ♚ 1 was the right move.", :exercise_id => 94, :move_number => 14, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 21:14:07", :piece_black => "...", :starting_coordinate_black => nil, :ending_coordinate_black => nil },
