  { :piece_white => "...", :starting_coordinate_white => nil, :ending_coordinate_white => nil, :explanation => "In order to keep the Black Queen from coming into the game.", :exercise_id => 94, :move_number => 21, :computer => nil, :created_at => "2013-07-17 20:41:23", :updated_at => "2013-07-17 20:41:23", :piece_black => "♜", :starting_coordinate_black => "a8", :ending_coordinate_black => "d8" },
