  { :piece_white => "♚", :starting_coordinate_white => "f2", :ending_coordinate_white => "e1", :explanation => nil, :exercise_id => 101, :move_number => 48, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♞", :starting_coordinate_black => "f6", :ending_coordinate_black => "d7" },
  { :piece_white => "♛", :starting_coordinate_white => "b6", :ending_coordinate_white => "d6", :explanation => nil, :exercise_id => 101, :move_number => 49, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♝", :starting_coordinate_black => "e4", :ending_coordinate_black => "c6" },
  { :piece_white => "♟", :starting_coordinate_white => "g3", :ending_coordinate_white => "g4", :explanation => nil, :exercise_id => 101, :move_number => 50, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♟", :starting_coordinate_black => "f5", :ending_coordinate_black => "g4" },
  { :piece_white => "♟", :starting_coordinate_white => "f4", :ending_coordinate_white => "f5", :explanation => nil, :exercise_id => 101, :move_number => 51, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♜", :starting_coordinate_black => "h2", :ending_coordinate_black => "h1" },
  { :piece_white => "♚", :starting_coordinate_white => "e1", :ending_coordinate_white => "d2", :explanation => nil, :exercise_id => 101, :move_number => 52, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♚", :starting_coordinate_black => "f7", :ending_coordinate_black => "e8" },
  { :piece_white => "♟", :starting_coordinate_white => "f5", :ending_coordinate_white => "f6", :explanation => nil, :exercise_id => 101, :move_number => 53, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♜", :starting_coordinate_black => "h1", :ending_coordinate_black => "h7" },
  { :piece_white => "♛", :starting_coordinate_white => "d6", :ending_coordinate_white => "e6", :explanation => nil, :exercise_id => 101, :move_number => 54, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♚", :starting_coordinate_black => "e8", :ending_coordinate_black => "f8" },
  { :piece_white => "♝", :starting_coordinate_white => "d4", :ending_coordinate_white => "e3", :explanation => nil, :exercise_id => 101, :move_number => 55, :computer => nil, :created_at => "2013-07-17 20:41:24", :updated_at => "2013-07-17 20:41:24", :piece_black => "♜", :starting_coordinate_black => "h7", :ending_coordinate_black => "f7" },
