  { :piece_white => "♛", :starting_coordinate_white => "h7", :ending_coordinate_white => "h8", :explanation => "", :exercise_id => 34, :move_number => 8, :computer => false, :created_at => "2013-07-24 22:20:01", :updated_at => "2013-07-24 22:20:01", :piece_black => "♞", :starting_coordinate_black => "e7", :ending_coordinate_black => "g8" },
