  { :piece_white => "♜", :starting_coordinate_white => "d1", :ending_coordinate_white => "e1", :explanation => "", :exercise_id => 34, :move_number => 10, :computer => false, :created_at => "2013-07-24 22:20:36", :updated_at => "2013-07-24 22:20:36", :piece_black => "♚", :starting_coordinate_black => "e7", :ending_coordinate_black => "d8" },
